import React from 'react';
import { connect } from 'react-redux';

import Button from '../button';
import '../App.css';
import Navbar from '../navbar';
import Loading from '../loading';
import { getDataCat } from '../../actions';

import './home.css'

class Home extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      dataCat: {},
    }
  }

  componentDidMount() {
    // this.getDataCat()รท
    // this.props.onLoad()
    this.props.getDataCat()
    console.log('token',  window.localStorage.getItem('jwt'))
    console.log('currentDataCat',  this.props.currentDataCat)
    console.log('getDataCat',  this.props.getDataCat())
  }

  getDataCat() {
    fetch('https://api.thecatapi.com/v1/images/search', {
      method: 'GET',
    })
    .then(response => response.json())
    .then(data => this.setState({ dataCat: data }))
  }

  handleClick = buttonName => {
    // this.setState(calculate(this.state, buttonName));
    console.log('123')
  };

  renderCat(data) {
    console.log('daadadadad', data)
    return (
      <div>
        <img src={data.url} alt="Paris" width="300" height="300" />
      </div>
    )
  }

  render() {
    const { currentDataCat } = this.props
    const checkdata =  currentDataCat && currentDataCat.length ? true : false

    return (
      <div className="home-wrapper">
        <Navbar />
        <div className="home-content">
          {checkdata ? currentDataCat.map( data => this.renderCat(data) ) : null}
          <Button text='Get Infomation' />
          <Loading />
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    currentDataCat: state.home.dataCat,
  }};

const mapDispatchToProps = {
  getDataCat: getDataCat,
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home)
