import React from 'react';
import { connect } from 'react-redux';

import '../App.css';
import Navbar from '../navbar';
import Loading from '../loading';
import { getDataCat, getDataDog } from '../../actions';

import './home.css'

class Home extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      dataCat: {},
    }
  }

  componentDidMount() {
    const { getDataCat } = this.props;

    getDataCat()
  }

  getDataCat() {
    fetch('https://api.thecatapi.com/v1/images/search', {
      method: 'GET',
    })
    .then(response => response.json())
    .then(data => this.setState({ dataCat: data }))
  }

  handleClick = buttonName => {
    // this.setState(calculate(this.state, buttonName));
    console.log('123')
  };

  renderCat(data) {
    console.log('daadadadad', data)
    return (
      <div>
        <img src={data.url} alt="Paris" width="300" height="300" />
      </div>
    )
  }

  render() {
    const { currentDataCat } = this.props
    const checkdata =  currentDataCat && currentDataCat.length ? true : false

    return (
      <div className="home-wrapper">
        <Navbar />
        <div className="home-content">
          <Loading />
          {checkdata ? currentDataCat.map( data => this.renderCat(data) ) : null}
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    currentDataCat: state.home.dataCat,
  }};

const mapDispatchToProps = {
  getDataCat,
  getDataDog,
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home)
