import React from 'react';
import { connect } from 'react-redux';

import Button from '../button';
import '../App.css';
import Navbar from '../navbar';
import { GET_DATA_CAT } from '../../actions';

import './home.css'

class Home extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      dataCat: {},
    }
  }

  componentDidMount() {
    this.getDataCat()
    // this.props.onLoad()
    console.log('token',  window.localStorage.getItem('jwt'))
    console.log('this.props.onLoad()',  this.props.onLoad())
  }

  getDataCat() {
    fetch('https://api.thecatapi.com/v1/images/search', {
      method: 'GET',
    })
    .then(response => response.json())
    .then(data => this.setState({ dataCat: data }))
  }

  handleClick = buttonName => {
    // this.setState(calculate(this.state, buttonName));
    console.log('123')
  };

  renderCat(data) {
    console.log('daadadadad', data)
    return (
      <div>
        <img src={data.url} alt="Paris" width="300" height="300" />
      </div>
    )
  }

  render() {
    console.log('data', this.state.dataCat)

    const { dataCat } = this.state

    return (
      <div className="home-wrapper">
        <Navbar />
        <div className="home-content">
          {dataCat && dataCat.length ? dataCat.map( data => this.renderCat(data) ) : null}
          <Button text='Get Infomation' />
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    currentUser: state.home.currentUser,
  }};

const mapDispatchToProps = dispatch => ({
  onLoad: () =>
    dispatch({ type: GET_DATA_CAT }),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home)
