import React from 'react';
import { connect } from 'react-redux';

import '../App.css';
import Navbar from '../navbar';
import Loading from '../loading';
import { getDataCat, getDataDog } from '../../actions';

import './home.css'

class Home extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      dataCat: {},
    }
  }

  componentDidMount() {
    const { getDataCat, getDataDog } = this.props;
    getDataDog()
    getDataCat()
  }

  renderCat(data) {
    console.log('daadadadad', data)
    return (
      <div>
        <img src={data.url} alt="Paris" width="400" height="300" />
      </div>
    )
  }

  render() {
    const { currentDataCat, currentDataDog } = this.props
    const checkdata =  currentDataCat && currentDataCat.length ? true : false

    return (
      <div className='home-wrapper'>
        <Navbar />
        <div className='home-content'>
          <Loading />
          <div className='content'>
            <div>
              {checkdata ? currentDataCat.map( data => this.renderCat(data) ) : null}
            </div>
            <div>
              <img src={currentDataDog} alt="Paris" width="400" height="300" />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    currentDataCat: state.home.dataCat,
    currentDataDog: state.home.dataDog,
  }};

const mapDispatchToProps = {
  getDataCat,
  getDataDog,
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home)
