}

const mapStateToProps = state => {
  return {
    currentDataCat: state.home.dataCat,
    currentDataDog: state.home.dataDog,
    loading: state.home.loading
  }};

const mapDispatchToProps = {
  getDataCat,
  getDataDog,
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home)
