import {
  GET_DATA_CAT,
} from '../actions';

import { helloSaga } from '../sagas'

const defaultState = {
  appName: 'Conduit',
  token: null,
  viewChangeCounter: 0
};

export default (state = defaultState, action) => {
  switch (action.type) {
    case 'GET_DATA_CAT':
      return { ...state, loading: true };
    default:
      return state;
  }
};
