import React from 'react';
import { connect } from 'react-redux';

import Input from '../input'
import Button from '../button'

import './login.css';

class Login extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      inputClass: 'input',
      errorInput: false,
    }
  }

  onLogin() {
    const { userName, password } = this.props
    const checkInfoLogin = userName && password && userName.length && password.length
    if (checkInfoLogin) {
      this.props.history.push('/home')
    } else {
      this.setState({ errorInput: true })
    }
  };

  render() {
    const { inputClass } = this.state
    return (
      <div className="login-wrapper">
        <div className="left-content-login">
          <span className="text-login-left">Hello! Wellcome to Demo</span>
        </div>
        <div className="form-login">
          <Input
            id='user-name'
            classInput={inputClass}
            type='text'
            placeholder='Enter Username'
          />
          <Input
            id='pass-word'
            classInput={inputClass}
            type='password'
            placeholder='Enter Password'
          />
          <Button text='Login' handleClick={() => this.onLogin()} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    userName: state.login.userName,
    password: state.login.password,
  }};

const mapDispatchToProps = dispatch => {
  return {
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login)
