import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/fontawesome-free-solid/faBars'

import logo from '../../assets/images/logo.png'
import Item from '././item'
import Lead from '././lead'

import './navbar.css'

export default class Navbar extends Component {
  constructor(props) {
    super(props)
    this.state = {
      menuClass: '',
    }
  }

  setToggleTopMenuClass = () => {
    if (this.state.menu_class === '') {
      this.setState({
        menuClass: 'toggled',
      })
    } else {
      this.setState({
        menuClass: '',
      })
    }
  }

  render = () => {
    let topMenuClass = `top-menu ${this.state.menuClass}`
    return (
      <div className={topMenuClass} >
        <Lead url={logo} />
        <div className='left'>
          <Item text='Home' path='/home' />
          <Item text='Dog' path='/dog' />
          <Item text='Cat' path='/cat'/>
          <Item text='About' path='/about' />
          <Item text='Contact' path='/contact'/>
        </div>
        <div className='right'>
          <Item text='Login' path='/' />
        </div>
        <FontAwesomeIcon
          icon={faBars}
          className='top-menu-icon'
          onClick={this.setToggleTopMenuClass}
        />
        <div className='clear-fix' />
      </div>
    )
  }
}
